arguments.argument:
{
    receiveCompleteMessage(global.serverSocket, 4);
    keyState = read_ubyte(global.serverSocket);
    aimDirection = read_ushort(global.serverSocket)*360/65536;
    aimDistance = read_ubyte(global.serverSocket)*2;

    var temp, newIntel;
    if(global.updateType == QUICK_UPDATE) or (global.updateType == FULL_UPDATE) {
        receiveCompleteMessage(global.serverSocket, 9);
        x = read_ushort(global.serverSocket) / 5;
        y = read_ushort(global.serverSocket) / 5;
        hspeed = read_byte(global.serverSocket) / 8.5;
        vspeed = read_byte(global.serverSocket) / 8.5;
        xprevious = x;
        yprevious = y;

        hp = read_ubyte(global.serverSocket);
        currentWeapon.ammoCount = read_ubyte(global.serverSocket);

        temp = read_ubyte(global.serverSocket);
        cloak = (temp & $01 != 0);
        moveStatus = (temp >> 1) & $07;
    }

    if(global.updateType == FULL_UPDATE){
        receiveCompleteMessage(global.serverSocket, 7);
        animationOffset = read_ubyte(global.serverSocket);
        // class specific syncs
        switch(player.class)
        {
        case CLASS_SPY:
            cloakAlpha = read_ubyte(global.serverSocket)/255;
            break;
        case CLASS_MEDIC:
            currentWeapon.uberCharge = read_ubyte(global.serverSocket)*2000/255;
            break;
        case CLASS_ENGINEER:
            nutsNBolts = read_ubyte(global.serverSocket);
            break;
        case CLASS_SNIPER:
            currentWeapon.t = read_ubyte(global.serverSocket);
            if(currentWeapon.t) zoomed = true; // Quick hack
            break;
        default:
            read_ubyte(global.serverSocket)
        }
        alarm[1] = read_short(global.serverSocket)/global.delta_factor; 
        if (alarm[1] != 0)
            canGrabIntel = false;
        intel = read_ubyte(global.serverSocket);
        intelRecharge = read_short(global.serverSocket);
        with(currentWeapon) {
            event_user(13);
        }
    }
    event_user(1);
}
