arguments.argument:
if (global.updateType == FULL_UPDATE)
{
    receiveCompleteMessage(global.serverSocket, 6);
    redWins = read_ubyte(global.serverSocket);
    blueWins = read_ubyte(global.serverSocket);
    state = read_ubyte(global.serverSocket);
    winners = read_byte(global.serverSocket);
    endCount = read_ushort(global.serverSocket);
}

receiveCompleteMessage(global.serverSocket, 8);
global.timeLimitMins = read_ubyte(global.serverSocket);
timeLimit = global.timeLimitMins*30*60;
timer = read_uint(global.serverSocket);
cpUnlock = read_ushort(global.serverSocket);
ArenaControlPoint.locked = cpUnlock > 0;
roundStart = read_ubyte(global.serverSocket)*2;
with (ArenaControlPoint) event_user(13);
